import {
  ADD_ARTICLE,
  EDIT_ARTICLE,
  UPDATE_ARTICLE,
  DELETE_ARTICLE,
  GET_ARTICLE,
  GET_ARTICLES,
  ARTICLE_LOADING
} from "../actions/types";

const intialState = {
  articles: [],
  article: {},
  loading: false
};

export default function(state = intialState, action) {
  switch (action.type) {
    case ARTICLE_LOADING:
      return {
        ...state,
        loading: true
      };
    case ADD_ARTICLE:
      return { ...state, articles: [action.payload, ...state.articles] };
    case GET_ARTICLES:
      return { ...state, articles: action.payload, loading: false };
    case GET_ARTICLE:
      return { ...state, article: action.payload, loading: false };
    case EDIT_ARTICLE:
      return {
        ...state,
        articles: state.articles.map(article => article._id !== action.payload)
      };
    case UPDATE_ARTICLE:
      return state.map(article => {
        if (article.id === action.payload) {
          return {
            ...state,
            title: action.articleData.title,
            image: action.articleData.image,
            imgSource: action.articleData.imgSource,
            text: action.articleData.text,
            category: action.articleData.category
          };
        } else return state;
      });
    case DELETE_ARTICLE:
      return {
        ...state,
        articles: state.articles.filter(
          article => article._id !== action.payload
        )
      };
    default:
      return state;
  }
}
