import React from "react";
import AxiosAPI from "../AxiosAPI";

class Edit extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      image: "",
      imgSource: "",
      category: "",
      text: ""
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles/show/" + this.props.match.params.id)
      .then(res => {
        this.setState({
          title: res.data.title,
          image: res.data.image,
          imgSource: res.data.imgSource,
          category: res.data.category,
          text: res.data.text
        });
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  onChangeTitle = event => {
    this.setState({
      title: event.target.value
    });
  };

  onChange = event => {
    switch (event.target.name) {
      case "image":
        this.setState({ image: event.target.files[0] });
        break;
      default:
        this.setState({ [event.target.name]: event.target.value });
    }
  };

  onChangeImgSource = event => {
    this.setState({
      imgSource: event.target.value
    });
  };

  onChangeText = e => {
    this.setState({ text: e.target.value });
  };

  onChangeCategory = e => {
    this.setState({ category: e.target.value });
  };

  onSubmit = event => {
    event.preventDefault();

    const obj = {
      title: this.state.title,
      image: this.state.image,
      imgSource: this.state.imgSource,
      text: this.state.text
    };
    console.log(obj);

    AxiosAPI.put("/api/articles/edit/" + this.props.match.params.id, obj).then(
      result => {
        this.props.history.push(
          "/api/articles/show/" + this.props.match.params.id
        );
      }
    );
  };

  render() {
    return (
      <div className="form-container">
        <div className="login">
          <form className="form" onSubmit={this.onSubmit}>
            <div className="form__group">
              <label className="form__label">Article Title</label>
              <input
                className="form__input"
                type="text"
                value={this.state.title}
                onChange={this.onChangeTitle}
                placeholder="Enter Article Title"
              />
            </div>
            <div>
              <div className="form__group">
                <label className="form__label">Image Source:</label>
                <input
                  className="form__input"
                  type="text"
                  value={this.state.imgSource}
                  onChange={this.onChangeImgSource}
                  placeholder="Enter Image Source"
                />
              </div>
              <label className="form__label">Image</label>
              <div className="form__group">
                <input
                  className="form__input"
                  type="file"
                  name="image"
                  accept="image/*"
                  onChange={this.onChange}
                />
              </div>

              <div className="form__group">
                <label className="form__label">Article Category</label>
                <input
                  type="text"
                  placeholder="Enter Article Category"
                  className="form__input"
                  onChange={this.onChangeCategory}
                  value={this.state.category}
                />
              </div>
              <div className="form__group">
                <label className="form__label">Content Field:</label>
                <textarea
                  name="text"
                  rows="30"
                  value={this.state.text}
                  onChange={this.onChangeText}
                  className="form__text"
                />
              </div>
            </div>
            <button className="submit-btn">Submit</button>
          </form>
        </div>
      </div>
    );
  }
}

export default Edit;
