import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addArticle } from "../../actions/articleActions";
import { convertFromRaw } from "draft-js";
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

const content = {
  entityMap: {},
  blocks: [
    {
      key: "637gr",
      text: "Initialized from content state.",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [],
      data: {}
    }
  ]
};

class ArticleForm extends React.Component {
  constructor(props) {
    super(props);
    const contentState = JSON.stringify(convertFromRaw(content));
    this.state = {
      title: "",
      imgSource: "",
      image: "",
      contentState: "",
      category: "",
      errors: {}
    };

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeImgSource = this.onChangeImgSource.bind(this);
    this.onChangeImage = this.onChangeImage.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillReceiveProps(newProps) {
    if (newProps) {
      this.setState({ errors: newProps.errors });
    }
  }

  onChangeTitle = e => {
    this.setState({ title: e.target.value });
  };

  onChangeImgSource = e => {
    this.setState({ imgSource: e.target.value });
  };

  onChangeImage = e => {
    this.setState({ image: e.target.files[0] });
  };

  onContentStateChange = contentState => {
    this.setState({
      contentState
    });
  };

  onChangeCategory = e => {
    this.setState({ category: e.target.value });
  };

  onSubmit(e) {
    e.preventDefault();

    let formData = new FormData();
    formData.append("title", this.state.title);
    formData.append("image", this.state.image);
    formData.append("imgSource", this.state.imgSource);
    formData.append("contentState", this.state.contentState);
    formData.append("category", this.state.category);

    this.props.addArticle(formData);
    this.setState({
      title: "",
      imgSource: "",
      image: " ",
      contentState: "",
      category: ""
    });
  }

  render() {
    const { contentState } = this.state;
    return (
      <div className="form-container">
        <div className="login">
          <h3 className>Create A New Article</h3>
          <form
            className="form"
            onSubmit={this.onSubmit}
            encType="multipart/form-data"
          >
            <label className="form__label">Article Title</label>
            <div className="form__group">
              <input
                type="text"
                placeholder="Enter Article Title"
                value={this.state.title}
                onChange={this.onChangeTitle}
                className="form__input"
              />
            </div>
            <div className="form__group">
              <label className="form__label">Upload Image</label>
              <input type="file" onChange={this.onChangeImage} />
            </div>
            <div className="form__group">
              <label className="form__label">Image Source</label>
              <input
                type="text"
                placeholder="Enter Image Source"
                value={this.state.imgSource}
                onChange={this.onChangeImgSource}
                className="form__input"
              />
            </div>
            <label className="form__label">Article Category</label>
            <div className="form__group">
              <input
                type="text"
                placeholder="Enter Article Category"
                className="form__input"
                onChange={this.onChangeCategory}
                value={this.state.category}
              />
            </div>
            <div className="form__group">
              <label className="form__label">Content</label>
              <Editor
                toolbarClassName="toolbarClassName"
                wrapperClassName="wrapperClassName"
                editorClassName="editorClassName"
                onContentStateChange={this.onContentStateChange}
                className="form__text"
              />
            </div>
            <button type="submit" className="submit-btn">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

ArticleForm.propTypes = {
  addArticle: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { addArticle }
)(ArticleForm);
