import React from "react";
import PropTypes from "prop-types";
import "../App.scss";
import { Link, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { setCurrentUser } from "../actions/authActions";
import { logoutUser } from "../actions/authActions";
import { addArticle } from "../actions/articleActions";
import logo from "../logo.svg";

class Header extends React.Component {
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };

  render() {
    const { user } = this.props.auth;
    const { articleWriter } = this.props.articleWriter;
    console.log(articleWriter);

    return (
      <div className="header">
        <div className="header__logo-box">
          <Link to="/">
            <img src={logo} className="header__logo" alt="Logo" />
          </Link>
        </div>
        <div className="login-status">
          <p className="login-status__text">Signed In As: {user.username}</p>
          <button className="submit-btn" onClick={this.onLogoutClick}>
            Logout
          </button>
        </div>
        <div className="header__text-box">
          <h1 className="heading-primary">
            <span className="heading-primary--main">Mind of A Millennial</span>
            <span className="heading-primary--sub">
              The People's Opinion on The Culture
            </span>
          </h1>
          <div>
            {user.id === "5d0e5b908b86ba21acf49470" ? (
              <Link to="/api/articles/add" className="btn btn--white">
                Add New Article
              </Link>
            ) : (
              <div />
            )}
          </div>
        </div>
      </div>
    );
  }
}

Header.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  articleWriter: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  articleWriter: state.writer
});

export default connect(
  mapStateToProps,
  { setCurrentUser, logoutUser, addArticle }
)(withRouter(Header));
