import { ADD_ARTICLE, GET_ARTICLE, DELETE_ARTICLE } from "./types";
import AxiosAPI from "../components/AxiosAPI";

export const createArticle = formValues => dispatch => {
  return dispatch => {
    return AxiosAPI.post("/api/articles/add", { ...formValues })
      .then(response => {
        dispatch(createArticleSuccess(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};

export const createArticleSuccess = data => {
  return {
    type: ADD_ARTICLE,
    payload: {
      _id: data._id,
      title: data.title,
      image: data.image,
      imgSource: data.imgSource,
      text: data.text
    }
  };
};

export const deleteArticleSuccess = id => {
  return {
    type: DELETE_ARTICLE,
    payload: {
      id
    }
  };
};

export const deleteArticle = id => {
  return dispatch => {
    return AxiosAPI.get("/api/articles/" + id)
      .then(response => {
        dispatch(deleteArticleSuccess(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};

export const getArticles = articles => {
  return {
    type: GET_ARTICLE,
    articles
  };
};

export const getAllArticles = () => {
  return dispatch => {
    return AxiosAPI.get("/api/articles")
      .then(response => {
        dispatch(getArticles(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};
