import React from "react";
import { Editor } from "@tinymce/tinymce-react";
import { Field, reduxForm } from "redux-form";

class ArticleForm extends React.Component {
  renderInput = ({ input, label, meta }) => {
    const className = `field ${meta.error && meta.touched ? "error" : ""}`;
    return (
      <div className={className}>
        <label>{label}</label>
        <input {...input} autoComplete="off" />
        {this.renderError(meta)}
      </div>
    );
  };

  onSubmit = formValues => {
    this.props.onSubmit(formValues);
  };

  handleEditorChange = content => {
    this.setState({ text: content });
  };

  render() {
    return (
      <div className="form-container">
        <div className="login">
          <form
            className="form"
            onSubmit={this.props.handleSubmit(this.onSubmit)}
            encType="multipart/form-data"
          >
            <div className="form__group">
              <Field
                className="form__input"
                name="title"
                component={this.renderInput}
                label="Enter Title"
                className="form__label"
              />
            </div>
            <div className="form__group">
              <Field
                className="form__input"
                name="imgSource"
                component={this.renderInput}
                label="Enter Image Source"
                className="form__label"
              />
            </div>
            <div className="form__group">
              <label className="form__label">Image:</label>
              <input
                className="form__input"
                type="file"
                name="image"
                accept=".jpg, .png, .jpeg"
                onChange={this.onChange}
              />
            </div>
            <div className="form__group">
              <label className="form__label">Content Field:</label>
              <Editor
                className="form__input"
                key="1"
                apiKey="rjadl5gyw4unfkgc83yjhwi2r89t35m1z43bun15yohisplr"
                init={{
                  forced_root_block: false,
                  plugins:
                    "advlist autolink autoresize lists link image charmap print preview anchor searchreplace visualblocks code fullscreen insertdatetime media table imagetools media wordcount"
                }}
                value={this.state.text}
                onEditorChange={this.handleEditorChange}
              />
            </div>
            <button type="submit" className="submit-btn">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default reduxForm({
  form: "articleForm"
})(ArticleForm);
