import React from "react";
import PropTypes from "prop-types";
import AxiosAPI from "../AxiosAPI";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { setCurrentUser } from "../../actions/authActions";
import { deleteArticle } from "../../actions/articleActions";

class Show extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      article: {}
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles/show/" + this.props.match.params.id).then(
      res => {
        this.setState({ article: res.data });
        console.log(this.state.article);
      }
    );
  }

  onDeleteClick(id) {
    this.props.deleteArticle(id);
  }

  render() {
    const { article, auth } = this.props;

    return (
      <div className="article-container">
        <h2 className="article-header">{article.title}</h2>
        <img
          className="article-img article-img--show"
          alt=""
          src={article.image}
        />
        <div>
          <p className="submitted-at">Submitted at {article.submittedAt}</p>
          <p>
            <cite className="img-source">{article.imgSource}</cite>
          </p>
          <p className="category">{article.category}</p>
        </div>
        <div className="article-content">
          <p>{article.text}</p>
        </div>

        {user.id === "5d0e5b908b86ba21acf49470" ||
        "5cdb45afcaf9b15b8f80cb68" ? (
          <React.Fragment>
            <button className="button2 button2--edit">
              <Link to={`/api/articles/edit/${this.state.article._id}`}>
                Edit Article
              </Link>
            </button>
            <button
              onClick={this.onDeleteClick.bind(this, article._id)}
              className="button2 button2--delete"
            >
              Delete Article
            </button>
          </React.Fragment>
        ) : (
          <div />
        )}
      </div>
    );
  }
}

Show.propTypes = {
  auth: PropTypes.object.isRequired,
  deleteArticle: PropTypes.func.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { setCurrentUser, deleteArticle }
)(Show);
