import React from "react";
import "./App.scss";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import setAuthToken from "./utils/setAuthToken";
import jwt_decode from "jwt-decode";

import { setCurrentUser, logoutUser } from "./actions/authActions";
import { Provider } from "react-redux";
import store from "./store";

import PrivateRoute from "./components/PrivateRoute";
import SideBar from "../src/components/Sidebar";
import ArticleForm from "../src/components/articles/ArticleForm";
import AllArticles from "../src/components/articles/AllArticles";
import SportsArticles from "../src/components/articles/Sports";
import TVMovies from "../src/components/articles/TV-Movies";
import Header from "./components/Header";
import Footer from "../src/components/Footer";
import Register from "./components/auth/Register";
import Login from "./components/auth/Login";
import AboutUs from "../src/components/AboutUs";
import Show from "../src/components/articles/Show";
import Edit from "../src/components/articles/Edit";

// Check for token to keep user logged in
if (localStorage.jwtToken) {
  // Set auth token header auth
  const token = localStorage.jwtToken;
  setAuthToken(token);
  // Decode token and get user info and exp
  const decoded = jwt_decode(token);
  // Set user and isAuthenticated
  store.dispatch(setCurrentUser(decoded));
  // Check for expired token
  const currentTime = Date.now() / 1000; // to get in milliseconds
  if (decoded.exp < currentTime) {
    // Logout user
    store.dispatch(logoutUser());

    // Redirect to login
    window.location.href = "./login";
  }
}

class App extends React.Component {
  render() {
    return (
      <Provider store={store}>
        <BrowserRouter>
          <div id="page-wrap">
            <SideBar />
            <Header />
            <Route path="/" exact component={AllArticles} />
            <Route path="/sports" exact component={SportsArticles} />
            <Route path="/tv-movies" exact component={TVMovies} />
            <Route path="/api/articles/show/:id" exact component={Show} />
            <Switch>
              <PrivateRoute
                path="/api/articles/add"
                exact
                component={ArticleForm}
              />
              <PrivateRoute
                path="/api/articles/edit/:id"
                exact
                component={Edit}
              />
            </Switch>
            <Route path="/about-us" exact component={AboutUs} />
            <Route path="/register" exact component={Register} />
            <Route path="/login" exact component={Login} />
            <Footer />
          </div>
        </BrowserRouter>
      </Provider>
    );
  }
}

export default App;
