import {
  ADD_ARTICLE,
  GET_ARTICLE,
  DELETE_ARTICLE,
  GET_ERRORS,
  CLEAR_ERRORS
} from "./types";
import AxiosAPI from "../components/AxiosAPI";

// Add Article
export const addArticle = articleData => dispatch => {
  dispatch(clearErrors());
  AxiosAPI.post("api/articles/add", articleData)
    .then(res =>
      dispatch({
        type: ADD_ARTICLE,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};

export const deleteArticleSuccess = id => {
  return {
    type: DELETE_ARTICLE,
    payload: {
      id
    }
  };
};

export const deleteArticle = id => dispatch => {
  AxiosAPI.delete(`/api/articles/${id}`)
    .then(res => {
      dispatch({
        type: DELETE_ARTICLE,
        payload: id
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      });
    });
};

export const getArticles = articles => {
  return {
    type: GET_ARTICLE,
    articles
  };
};

export const getAllArticles = () => {
  return dispatch => {
    return AxiosAPI.get("/api/articles")
      .then(response => {
        dispatch(getArticles(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};

// Clear errors
export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS
  };
};
