import React from "react";
import { Editor } from "@tinymce/tinymce-react";
import { withRouter } from "react-router-dom";
import AxiosAPI from "../AxiosAPI";

class ArticleForm extends React.Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeImgSource = this.onChangeImgSource.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    this.onChangeCategoryMusic = this.onChangeCategoryMusic.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    this.handleEditorChange = this.handleEditorChange.bind(this);

    this.state = {
      title: "",
      image: "",
      imgSource: "",
      text: "",
      category: ""
    };
  }

  onChangeTitle = event => {
    this.setState({
      title: event.target.value
    });
  };

  onChange = e => {
    switch (e.target.name) {
      case "image":
        this.setState({ image: e.target.files[0] });
        break;
      default:
        this.setState({ [e.target.name]: e.target.value });
    }
  };

  onChangeImgSource = event => {
    this.setState({
      imgSource: event.target.value
    });
  };

  onChangeCategory = event => {
    this.setState({
      category: event.target.value
    });
  };

  onChangeCategoryMusic = event => {
    this.setState({
      category: event.target.value
    });
  };

  onChangeCategory = event => {
    this.setState({
      category: event.target.value
    });
  };

  onChangeCategory = event => {
    this.setState({
      category: event.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    if (this.state.title.trim() && this.state.imgSource.trim() && this.state.text.trim()) {
      this.props.onAddArticle(this.state);
      this.handleReset();
    }
  };

  handleReset = () => {
    this.setState({
      title: "",
      image: "",
      imgSource: "",
      text: "",
    })
  }

  handleEditorChange = content => {
    this.setState({ text: content });
  };

  render() {
    return (
      <div className="form-container">
        <div className="login">
          <form className="form" onSubmit={this.onSubmit}>
            <div className="form__group">
              <label className="form__label">Article Title:</label>
              <input
                className="form__input"
                type="text"
                value={this.state.title}
                onChange={this.onChangeTitle}
                placeholder="Enter Article Title"
              />
            </div>
            <div className="form__group">
              <label className="form__label">Choose a Cateogry</label>
              <label className="form__label">Sports</label>
              <input
                value="Sports"
                type="checkbox"
                className="form-checkbox"
                id="check-one"
                checked={this.state.category === "Sports"}
                onChange={this.onChangeCategory}
              />
              <label className="form__label">Music</label>
              <input
                type="checkbox"
                className="form-checkbox"
                id="check-one"
                checked={this.state.category === "Music"}
                onChange={this.onChangeCategoryMusic}
              />
              <label className="form__label">TV/Movies</label>
              <input
                type="checkbox"
                className="form-checkbox"
                id="check-one"
                checked={this.state.category === "TV-Movies"}
                onChange={this.onChangeCategory}
              />
              <label className="form__label">Lifestyle</label>
              <input
                type="checkbox"
                className="form-checkbox"
                id="check-one"
                checked={this.state.category === "Lifestyle"}
                onChange={this.onChangeCategory}
              />
              <label className="form__label">Misc</label>
              <input
                type="checkbox"
                className="form-checkbox"
                id="check-one"
                checked={this.state.category === "Misc"}
                onChange={this.onChangeCategory}
              />
            </div>
            <div className="form__group">
              <label className="form__label">Image Source:</label>
              <input
                className="form__input"
                type="text"
                value={this.state.imgSource}
                onChange={this.onChangeImgSource}
                placeholder="Enter Image Source"
              />
            </div>
            <div className="form__group">
              <label className="form__label">Image:</label>
              <input
                className="form__input"
                type="file"
                name="image"
                accept="image/*"
                onChange={this.onChange}
              />
            </div>
            <div className="form__group">
              <label className="form__label">Content Field:</label>
              <Editor
                className="form__input"
                key="1"
                apiKey="rjadl5gyw4unfkgc83yjhwi2r89t35m1z43bun15yohisplr"
                init={{
                  forced_root_block: false,
                  plugins:
                    "advlist autolink autoresize lists link image charmap print preview anchor searchreplace visualblocks code fullscreen insertdatetime media table imagetools media wordcount"
                }}
                value={this.state.text}
                onEditorChange={this.handleEditorChange}
              />
            </div>
            <button type="submit" className="submit-btn">Submit</button>
            <button type="button" onClick={this.handleReset} className="submit-btn">Reset</button>
          </form>
        </div>
      </div>
    );
  }
}

export default withRouter(ArticleForm);
