import { ADD_ARTICLE, GET_ARTICLE, DELETE_ARTICLE } from "./types";
import history from "../history";
import AxiosAPI from "../components/AxiosAPI";

export const createArticle = formValues => async dispatch => {
  const response = await AxiosAPI.post("/api/articles/add", { ...formValues });

  dispatch({ type: ADD_ARTICLE, payload: response.data });
  history.push("/");
};

export const deleteArticleSuccess = id => {
  return {
    type: DELETE_ARTICLE,
    payload: {
      id
    }
  };
};

export const deleteArticle = id => {
  return dispatch => {
    return AxiosAPI.get("/api/articles/" + id)
      .then(response => {
        dispatch(deleteArticleSuccess(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};

export const getArticles = articles => {
  return {
    type: GET_ARTICLE,
    articles
  };
};

export const getAllArticles = () => {
  return dispatch => {
    return AxiosAPI.get("/api/articles")
      .then(response => {
        dispatch(getArticles(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};
