import React from "react";
import AxiosAPI from "../AxiosAPI";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { setCurrentUser } from "../../actions/authActions";

class Show extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      article: {}
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles/show/" + this.props.match.params.id).then(
      res => {
        this.setState({ article: res.data });
        console.log(this.state.article);
      }
    );
  }

  delete(id) {
    console.log(id);
    AxiosAPI.delete("/api/articles/" + id).then(result => {
      this.props.history.push("/");
    });
  }

  render() {
    const { user } = this.props.auth;
    return (
      <div className="article-container">
        <h2 className="article-header">{this.state.article.title}</h2>
        <img
          className="article-img article-img--show"
          alt=""
          src={this.state.article.image}
        />
        <p>
          <cite>{this.state.article.imgSource}</cite>
        </p>
        <p>{this.state.article.text}</p>
        {user} (
        <button className="button2 button2--edit">
          <Link to={`/api/articles/edit/${this.state.article._id}`}>
            Edit Article
          </Link>
        </button>
        <button
          onClick={this.delete.bind(this, this.state.article._id)}
          className="button2 button2--delete"
        >
          Delete Article
        </button>
        )
      </div>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { setCurrentUser }
)(withRouter(Show));
