import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { setCurrentUser } from "../../actions/authActions";
import {
  deleteArticle,
  editArticle,
  getArticle
} from "../../actions/articleActions";

class Show extends React.Component {
  componentDidMount() {
    this.props.getArticle(this.props.match.params.id);
  }

  onDeleteClick(id) {
    this.props.deleteArticle(id);
  }

  onEditClick(id) {
    this.props.editArticle(id);
  }

  render() {
    const { article, isAuthenticated, auth } = this.props;

    const editDeleteButtons = (
      <React.Fragment>
        <button
          onClick={this.onEditClick.bind(this, article._id)}
          className="button2 button2--edit"
        >
          Edit Article
        </button>
        <button
          onClick={this.onDeleteClick.bind(this, article._id)}
          className="button2 button2--delete"
        >
          Delete Article
        </button>
      </React.Fragment>
    );
    return (
      <div className="article-container">
        <h2 className="article-header">{article.title}</h2>
        <img
          className="article-img article-img--show"
          alt=""
          src={article.image}
        />
        <div>
          <p className="submitted-at">Submitted at {article.submittedAt}</p>
          <p>
            <cite className="img-source">{article.imgSource}</cite>
          </p>
          <p className="category">{article.category}</p>
        </div>
        <div className="article-content">
          <p>{article.text}</p>
        </div>

        {article.author.id === auth.user.id ? editDeleteButtons : <div />}
      </div>
    );
  }
}

Show.propTypes = {
  auth: PropTypes.object.isRequired,
  article: PropTypes.object.isRequired,
  deleteArticle: PropTypes.func.isRequired,
  editArticle: PropTypes.func.isRequired,
  getArticle: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { setCurrentUser, deleteArticle, editArticle, getArticle }
)(Show);
