import {
  ADD_ARTICLE,
  GET_ARTICLE,
  GET_ARTICLES,
  EDIT_ARTICLE,
  DELETE_ARTICLE,
  GET_ERRORS,
  ARTICLE_LOADING,
  CLEAR_ERRORS
} from "./types";
import AxiosAPI from "../components/AxiosAPI";

// Add Article
export const addArticle = articleData => dispatch => {
  dispatch(clearErrors());
  AxiosAPI.post("api/articles/add", articleData)
    .then(res =>
      dispatch({
        type: ADD_ARTICLE,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};

export const deleteArticle = id => dispatch => {
  AxiosAPI.delete(`/api/articles/${id}`)
    .then(res => {
      dispatch({
        type: DELETE_ARTICLE,
        payload: id
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ARTICLES,
        payload: err.response.data
      });
    });
};

export const editArticle = id => dispatch => {
  AxiosAPI.put(`/api/articles/${id}`)
    .then(res => {
      dispatch({
        type: EDIT_ARTICLE,
        payload: id
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ARTICLES,
        payload: err.response.data
      });
    });
};

export const getAllArticles = () => dispatch => {
  dispatch(setArticleLoading());
  AxiosAPI.get("/api/articles")
    .then(res =>
      dispatch({
        type: GET_ARTICLES,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ARTICLES,
        payload: null
      })
    );
};

export const getArticle = id => dispatch => {
  dispatch(
    AxiosAPI.get(`/api/articles/${id}`).then(res =>
      dispatch({
        type: GET_ARTICLE,
        payload: id
      })
    )
  ).catch(err =>
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    })
  );
};

// Set loading state
export const setArticleLoading = () => {
  return {
    type: ARTICLE_LOADING
  };
};

// Clear errors
export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS
  };
};
