import React from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";

class AllArticles extends React.Component {
  render() {
    const { articles } = this.props;
    return (
      <div className="container">
        <section className="leading">
          <p className="leading-bigtext">Welcome</p>
          <p className="leading-text">Check out our most recent articles!</p>
        </section>
        <main className="grid">
          {articles.map(article => {
            return (
              <div key={article._id}>
                <h3 className="u-center-text">{article.title}</h3>
                <Link
                  className="article-link"
                  to={`/api/articles/show/${article._id}`}
                >
                  <img className="article-img" alt="" src={article.image} />
                </Link>
              </div>
            );
          })}
        </main>
      </div>
    );
  }
}

AllArticles.propTypes = {
  articles: PropTypes.array.isRequired
};

export default AllArticles;
