import React from "react";
import { connect } from "react-redux";
import propTypes from "prop-types";
import { resetPassword } from "../../actions/authActions";

class ChangePassword extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      passwordChange: "",
      newPasswordChange: "",
      confirmNewPasswordChange: "",
      errors: {}
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const resetData = {
      password: this.state.passwordChange,
      newPassword: this.state.newPasswordChange,
      confirmNewPassword: this.state.confirmNewPasswordChange
    };
    this.props.resetPassword(resetData);
  };

  render() {
    const { errors } = this.props;
    console.log(errors);
    return (
      <section className="section-form">
        <div className="form-container">
          <div className="login">
            <div className="login__form">
              <h2 className="u-center-text">Change Your Password</h2>
              <form className="form" onSubmit={this.onSubmit}>
                <label className="form__label">Email Address</label>
                <div className="form__group">
                  <input
                    className="form__input"
                    type="text"
                    placeholder="Email Address"
                    value={this.state.email}
                    onChange={this.onChange}
                    error={errors.password}
                  />
                </div>
                <label className="form__label">Current Password</label>
                <div className="form__group">
                  <input
                    className="form__input"
                    type="password"
                    placeholder="Current Password"
                    value={this.state.passwordChange}
                    onChange={this.onChange}
                    error={errors.password}
                  />
                </div>
                <div className="form__group">
                  <label className="form__label" New Password>
                    New Password
                  </label>
                  <input
                    className="form__input"
                    type="password"
                    placeholder="New Password"
                    value={this.state.newPasswordChange}
                    onChange={this.onChange}
                    error={errors.newpassword}
                  />
                </div>
                <div className="form__group">
                  <label className="form__label">Confirm New Password</label>
                  <input
                    className="form__input"
                    type="password"
                    placeholder="Confirm New Password"
                    value={this.state.confirmNewPasswordChange}
                    onChange={this.onChange}
                    error={errors.confrimNewPassword}
                  />
                </div>
                <input
                  type="submit"
                  value="Change Password"
                  className="submit-btn"
                />
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

ChangePassword.propTypes = {
  resetPassword: propTypes.func.isRequired,
  auth: propTypes.object.isRequired,
  errors: propTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { resetPassword }
)(ChangePassword);
