import React from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import AxiosAPI from "../AxiosAPI";

class SportsArticles extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles/sports").then(res => {
      this.setState({
        articles: res.data
      });
      console.log(this.state.articles);
    });
  }

  render() {
    return (
      <div className="container">
        <section>
          <img src={process.env.PUBLIC_URL + "MoM-Sports-Banner@2x.png"} />
        </section>
        <main className="grid">
          {this.state.articles.map(article => {
            return (
              <div key={article._id}>
                <h3 className="u-center-text">{article.title}</h3>
                <Link
                  className="article-link"
                  to={`/api/articles/show/${article._id}`}
                >
                  <img
                    className="article-img"
                    alt="Main Image"
                    src={article.image}
                  />
                </Link>
              </div>
            );
          })}
        </main>
      </div>
    );
  }
}
SportsArticles.propTypes = {
  articles: PropTypes.array.isRequired
};

export default SportsArticles;
