"use strict";

exports.removeLeadingAndTrailingHTTPWhitespace = function (string) {
  return string.replace(/^[ \t\n\r]+/, "").replace(/[ \t\n\r]+$/, "");
};

exports.removeTrailingHTTPWhitespace = function (string) {
  return string.replace(/[ \t\n\r]+$/, "");
};

exports.isHTTPWhitespaceChar = function (char) {
  return char === " " || char === "\t" || char === "\n" || char === "\r";
};

exports.solelyContainsHTTPTokenCodePoints = function (string) {
  return /^[-!#$%&'*+.^_`|~A-Za-z0-9]*$/.test(string);
};

exports.soleyContainsHTTPQuotedStringTokenCodePoints = function (string) {
  return /^[\t\u0020-\u007E\u0080-\u00FF]*$/.test(string);
};

exports.asciiLowercase = function (string) {
  return string.replace(/[A-Z]/g, function (l) {
    return l.toLowerCase();
  });
};
