import React from "react";
import AxiosAPI from "../AxiosAPI";
import { Link } from "react-router-dom";
import ReactPaginate from "react-paginate";

class AllArticles extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles")
      .then(res => {
        this.setState({ articles: res.data });
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  render() {
    return (
      <div className="container">
        <section className="leading">
          <p className="leading-bigtext">Welcome</p>
          <p className="leading-text">Check out our most recent articles!</p>
        </section>
        <main className="grid">
          {this.state.articles.map(article => (
            <div key={article._id}>
              <h3 className="u-center-text">{article.title}</h3>
              <Link
                className="article-link"
                to={`/api/articles/show/${article._id}`}
              >
                <img className="article-img" alt="" src={article.image} />
              </Link>
            </div>
          ))}
        </main>
        <ReactPaginate
          previousLabel={"previous"}
          nextLabel={"next"}
          breakLabel={"..."}
          breakClassName={"break-me"}
          pageCount={this.state.pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={this.handlePageClick}
          containerClassName={"pagination"}
          subContainerClassName={"pages pagination"}
          activeClassName={"active"}
          pageClassName={"react-paginate-li"}
        />
      </div>
    );
  }
}

export default AllArticles;
