import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { deleteArticle } from "../../actions";

function AllArticles({ articles, onDelete }) {
  if (!articles.length) {
    return <div>No Posts</div>;
  }

  return (
    <div className="container">
      <section className="leading">
        <p className="leading-bigtext">Welcome</p>
        <p className="leading-text">Check out our most recent articles!</p>
      </section>
      <main className="grid">
        {articles.map(article => {
          <div>
            <h3 className="u-center-text">{article.title}</h3>
            <Link
              className="article-link"
              to={`/api/articles/show/${article._id}`}
            >
              <img className="article-img" alt="" src={article.image} />
            </Link>
            <button onDelete={onDelete} className="submit-btn">
              Delete Article
            </button>
          </div>;
        })}
      </main>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    articles: state.articles
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onDelete: id => {
      dispatch(deleteArticle(id));
    }
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AllArticles);
