import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Editor } from "@tinymce/tinymce-react";
import { addArticle } from "../../actions/articleActions";

class ArticleForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      imgSource: "",
      image: "",
      text: "",
      errors: {}
    };

    this.onChange = this.onChange.bind(this);
    this.onChangeImage = this.onChangeImage.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillReceiveProps(newProps) {
    if (newProps) {
      this.setState({ errors: newProps.errors });
    }
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onChangeImage = e => {
    this.setState({ image: e.target.files[0] });
  };

  onSubmit(e) {
    e.preventDefault();

    const { user } = this.props.auth;

    const newArticle = {
      title: this.state.title,
      imgSource: this.state.imgSource,
      image: this.state.image,
      text: this.state.text
    };

    this.props.addPost(newArticle);
    this.setState({ title: "", imgSource: "", image: " ", text: "" });
  }

  handleEditorChange = content => {
    this.setState({ text: content });
  };

  render() {
    return (
      <div className="form-container">
        <div className="login">
          <form
            className="form"
            onSubmit={this.onSubmit}
            encType="multipart/form-data"
          >
            <div className="form__group">
              <input
                type="text"
                placeholder="Enter Article Title"
                onChange={this.onChange}
                className="form__input"
              />
            </div>
            <div className="form__group">
              <label className="form__label">Upload Image</label>
              <input type="file" onChange={this.onChangeImage} />
            </div>
            <div className="form__group">
              <input
                type="text"
                placeholder="Enter Image Source"
                onChange={this.onChange}
                className="form__input"
              />
            </div>

            <div className="form__group">
              <Editor
                apiKey="rjadl5gyw4unfkgc83yjhwi2r89t35m1z43bun15yohisplr"
                init={{
                  plugins:
                    "advlist autolink autoresize lists link image charmap print"
                }}
                value={this.state.text}
                className="form__input"
                onEditorChange={this.handleEditorChange}
              />
            </div>
            <button type="submit" className="submit-btn">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

ArticleForm.propTypes = {
  addArticle: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { addArticle }
)(ArticleForm);
