import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { setCurrentUser } from "../../actions/authActions";
import {
  getArticle,
  deleteArticle,
  editArticle
} from "../../actions/articleActions";

class Show extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      article: {}
    };
  }

  componentDidMount() {
    this.props.getArticle(this.props.match.params.id);
  }

  onDeleteClick(id) {
    this.props.deleteArticle(id);
  }

  onEditClick(id) {
    this.props.editArticle(id);
  }

  render() {
    const { article, auth } = this.props;

    return (
      <div className="article-container">
        <h2 className="article-header">{this.state.article.title}</h2>
        <img
          className="article-img article-img--show"
          alt=""
          src={this.state.article.image}
        />
        <div>
          <p className="submitted-at">
            Submitted at {this.state.article.submittedAt}
          </p>
          <p>
            <cite className="img-source">{this.state.article.imgSource}</cite>
          </p>
          <p className="category">{this.state.article.category}</p>
        </div>
        <div className="article-content">
          <p>{this.state.article.text}</p>
        </div>

        {article.author === auth.username ? (
          <React.Fragment>
            <button
              className="button2 button2--edit"
              onClick={this.onEditClick.bind(this, article._id)}
            >
              Edit Article
            </button>
            <button
              onClick={this.onDeleteClick.bind(this, article._id)}
              className="button2 button2--delete"
            >
              Delete Article
            </button>
          </React.Fragment>
        ) : (
          <div />
        )}
      </div>
    );
  }
}

Show.propTypes = {
  auth: PropTypes.object.isRequired,
  deleteArticle: PropTypes.func.isRequired,
  editArticle: PropTypes.func.isRequired,
  getArticle: PropTypes.func.isRequired,
  article: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  article: state.article
});

export default connect(
  mapStateToProps,
  { setCurrentUser, deleteArticle, getArticle, editArticle }
)(Show);
