import {
  ADD_ARTICLE,
  GET_ARTICLE,
  GET_ARTICLES,
  EDIT_ARTICLE,
  DELETE_ARTICLE,
  ARTICLE_LOADING,
  GET_ERRORS,
  CLEAR_ERRORS
} from "./types";
import AxiosAPI from "../components/AxiosAPI";

// Add Article
export const addArticle = articleData => dispatch => {
  dispatch(clearErrors());
  AxiosAPI.post("api/articles/add", articleData)
    .then(res =>
      dispatch({
        type: ADD_ARTICLE,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};

export const getAllArticles = () => dispatch => {
  dispatch(setArticleLoading());
  AxiosAPI.get("/api/articles")
    .then(res =>
      dispatch({
        type: GET_ARTICLES,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ARTICLES,
        payload: null
      })
    );
};

export const getArticle = id => dispatch => {
  dispatch(setArticleLoading());
  AxiosAPI.get(`/api/articles/show/${id}`)
    .then(res =>
      dispatch({
        type: GET_ARTICLE,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ARTICLE,
        payload: null
      })
    );
};

export const editArticle = (articleData, id) => dispatch => {
  dispatch(setArticleLoading());
  AxiosAPI.get(`/api/articles/edit/${id}`, articleData).then(res =>
    dispatch({
      type: EDIT_ARTICLE,
      payload: res.data
    })
  );
};

export const deleteArticle = id => async dispatch => {
  try {
    const res = await AxiosAPI.delete(`/api/articles/${id}`);

    dispatch({
      type: DELETE_ARTICLE,
      payload: id
    });
    dispatch(setAlert("Article Removed", "success"));
  } catch (err) {
    dispatch({
      type: ARTICLE_ERROR,
      payload: err.response
    });
  }
};

// Set loading state
export const setArticleLoading = () => {
  return {
    type: ARTICLE_LOADING
  };
};

// Clear errors
export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS
  };
};
