import {
  GET_ERRORS,
  CLEAR_ERRORS,
  ADD_ERROR,
  REMOVE_ERROR
} from "../actions/types";

const initialState = {};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_ERRORS:
      return action.payload;
    case CLEAR_ERRORS:
      return {};
    default:
      return state;
  }
}
