import React from "react";
import AxiosAPI from "../AxiosAPI";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";

class AllArticles extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      activePage: 15
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles")
      .then(res => {
        this.setState({ articles: res.data });
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  handlePageChange(pageNumber) {
    console.log(`active page is ${pageNumber}`);
    this.setState({ activePage: pageNumber });
  }

  render() {
    return (
      <div className="container">
        <section className="leading">
          <p className="leading-bigtext">Welcome</p>
          <p className="leading-text">Check out our most recent articles!</p>
        </section>
        <main className="grid">
          {this.state.articles.map(article => (
            <div key={article._id}>
              <h3 className="u-center-text">{article.title}</h3>
              <Link
                className="article-link"
                to={`/api/articles/show/${article._id}`}
              >
                <img className="article-img" alt="" src={article.image} />
              </Link>
            </div>
          ))}
        </main>
        <Pagination
          activePage={this.state.activePage}
          itemsCountPerPage={10}
          totalItemsCount={450}
          pageRangeDisplayed={5}
          onChange={this.handlePageChange}
          innerClass={"pagination"}
        />
      </div>
    );
  }
}

export default AllArticles;
