import React from "react";
import AxiosAPI from "../AxiosAPI";
import axios from 'axios';
import { Link } from "react-router-dom";

class AllArticles extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }

  componentDidMount() {
    axios.defaults.headers.common['Authorization'] = localStorage.getItem('jwtToken');
    AxiosAPI.get("/api/articles")
      .then(res => {
        this.setState({ articles: res.data });
      })
      .catch(function(error) {
        if(error.response.status === 401){
          this.props.history.push('/login');
        }
      });
  }

  logout = () => {
    localStorage.removeItem('jwtToken');
    window.location.reload();
  }

  render() {
    return (
      <div class="container">
        <section className="leading">
          <p class="leading-bigtext">Welcome</p>
          <p class="leading-text">Check out our most recent articles!</p>
        </section>
        {localStorage.getItem('jwtToken') &&
                <button className="btn btn--white" onClick={this.logout}>Logout</button>
              }
        <main class="grid">
          {this.state.articles.map(article => (
            <div>
              <h3 className="u-center-text">{article.title}</h3>
              <Link
                className="article-link"
                to={`/api/articles/show/${article._id}`}
              >
                <img className="article-img" alt="" src={article.image} />
              </Link>
            </div>
          ))}
        </main>
      </div>
    );
  }
}

export default AllArticles;
