import {
  ADD_ARTICLE,
  ADD_ARTICLE_SUCCESS,
  GET_ARTICLE,
  GET_ARTICLE_SUCCESS,
  GET_ARTICLES,
  GET_ARTICLES_SUCCESS,
  EDIT_ARTICLE,
  EDIT_ARTICLE_SUCCESS,
  DELETE_ARTICLE,
  GET_ERRORS,
  CLEAR_ERRORS
} from "./types";
import AxiosAPI from "../components/AxiosAPI";

// Add Article
export const addArticle = articleData => dispatch => {
  dispatch(clearErrors());
  AxiosAPI.post("api/articles/add", articleData)
    .then(res =>
      dispatch({
        type: ADD_ARTICLE,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};

export function deleteArticle(articleId) {
  return function(dispatch) {
    AxiosAPI.delete(`api/articles/` + articleId)
      .then(res => {
        dispatch(deleteArticleSuccess(res.data));
        history.push("/");
      })
      .catch(err => {
        console.log(err);
      });
  };
}

export function deleteArticleSuccess(article) {
  return {
    type: "DELETE_ARTICLE_SUCCESS",
    article
  };
}

export function editArticle(title, image, imgSource, text, category) {
  return function(dispatch) {
    AxiosAPI.put(`api/articles/edit/` + articleId, {
      title,
      image,
      imgSource,
      text,
      category
    })
      .then(res => {
        dispatch(editArticleSuccess(res.data));
        history.push("/");
      })
      .catch(err => {
        console.log(err);
      });
  };
}

export function editArticleSuccess(article) {
  return {
    type: "EDIT_ARTICLE_SUCCESS",
    article
  };
}

export function getAllArticles() {
  return function(dispatch) {
    AxiosAPI.get(`api/articles`)
      .then(res => {
        dispatch(getAllArticlesSuccess(res.data));
      })
      .catch(err => {
        console.log(err);
      });
  };
}

export function getAllArticlesSuccess(articles) {
  return {
    type: "GET_ARTICLES_SUCCESS",
    articles
  };
}

export function getArticle(articleId) {
  return function(dispatch) {
    AxiosAPI.get(`api/articles/show/` + articleId)
      .then(res => {
        dispatch(getArticleSuccess(res.data));
      })
      .catch(err => {
        console.log(err);
      });
  };
}

export function getArticleSuccess(article) {
  return {
    type: "GET_ARTICLE_SUCCESS",
    article
  };
}

// Clear errors
export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS
  };
};
