import React from "react";
import AxiosAPI from "../AxiosAPI";
import { Editor } from "@tinymce/tinymce-react";

class Edit extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      image: "",
      imgSource: "",
      text: ""
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles/show/" + this.props.match.params.id)
      .then(res => {
        this.setState({
          title: res.data.title,
          image: res.data.image,
          imgSource: res.data.imgSource,
          text: res.data.text
        });
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  onChangeTitle = event => {
    this.setState({
      title: event.target.value
    });
  };

  onChange = event => {
    switch (event.target.name) {
      case "image":
        this.setState({ image: event.target.files[0] });
        break;
      default:
        this.setState({ [event.target.name]: event.target.value });
    }
  };

  onChangeImgSource = event => {
    this.setState({
      imgSource: event.target.value
    });
  };

  onSubmit = event => {
    event.preventDefault();

    const obj = {
      title: this.state.title,
      image: this.state.image,
      imgSource: this.state.imgSource,
      text: this.state.text
    };
    console.log(obj);

    AxiosAPI.put("/api/articles/edit/" + this.props.match.params.id, obj).then(
      result => {
        this.props.history.push(
          "/api/articles/show/" + this.props.match.params.id
        );
      }
    );
  };

  handleEditorChange = content => {
    this.setState({ text: content });
  };

  render() {
    return (
      <div className="form-container">
        <form onSubmit={this.onSubmit}>
          <div className="form" />
          <div>
            <label className="label">Article Title</label>
            <input
              className="input-res"
              type="text"
              value={this.state.title}
              onChange={this.onChangeTitle}
              placeholder="Enter Article Title"
            />
          </div>
          <div>
            <label className="label">Image Source:</label>
            <input
              className="input-res"
              type="text"
              value={this.state.imgSource}
              onChange={this.onChangeImgSource}
              placeholder="Enter Image Source"
            />
          </div>
          <div>
            <input
              type="file"
              name="image"
              accept="image/*"
              onChange={this.onChange}
            />
          </div>

          <div>
            <label>Content Field:</label>
            <Editor
              key="1"
              apiKey="rjadl5gyw4unfkgc83yjhwi2r89t35m1z43bun15yohisplr"
              init={{
                forced_root_block: false,
                plugins:
                  "advlist autolink autoresize lists link image charmap print preview anchor searchreplace visualblocks code fullscreen insertdatetime media table imagetools media wordcount"
              }}
              value={this.state.text}
              onEditorChange={this.handleEditorChange}
            />
          </div>
          <button className="submit-btn">Submit</button>
        </form>
      </div>
    );
  }
}

export default Edit;
