import React from "react";
import { Link } from "react-router-dom";
import AxiosAPI from "../AxiosAPI";

class AllArticles extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles/").then(res => {
      this.setState({
        articles: res.data
      });
    });
  }

  render() {
    document.title = "Mind of A Millennial | Home";
    return (
      <div className="container">
        <section className="leading">
          <p className="leading-bigtext">Welcome</p>
          <p className="leading-text">Check out our most recent articles!</p>
        </section>
        <main className="grid">
          {this.state.articles.slice(-16).map(article => {
            return (
              <div key={article._id}>
                <h3 className="u-center-text">{article.title}</h3>
                <Link
                  className="article-link"
                  to={`/api/articles/show/${article._id}`}
                >
                  <img
                    className="article-img"
                    alt="Article Img"
                    src={article.image}
                  />
                </Link>
              </div>
            );
          })}
        </main>
      </div>
    );
  }
}

export default AllArticles;
