import React from "react";
import AxiosAPI from "../AxiosAPI";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { setCurrentUser } from "../../actions/authActions";

class Show extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      article: {}
    };
  }

  componentDidMount() {
    AxiosAPI.get("/api/articles/show/" + this.props.match.params.id).then(
      res => {
        this.setState({ article: res.data });
      }
    );
  }

  delete(id) {
    AxiosAPI.delete("/api/articles/" + id).then(result => {
      this.props.history.push("/");
    });
  }

  onEditClick(id) {
    AxiosAPI.put("/api/articles/" + id).then(result => {
      this.props.history.push("/");
    });
  }

  render() {
    const { isAuthenticated, user } = this.props.auth;
    console.log(user);

    const editDeleteButtons = (
      <React.Fragment>
        <button
          onClick={this.onEditClick.bind(this, article._id)}
          className="button2 button2--edit"
        >
          Edit Article
        </button>
        <button
          onClick={this.delete.bind(this, article._id)}
          className="button2 button2--delete"
        >
          Delete Article
        </button>
      </React.Fragment>
    );
    return (
      <div className="article-container">
        <h2 className="article-header">{this.state.article.title}</h2>
        <img
          className="article-img article-img--show"
          alt=""
          src={this.state.article.image}
        />
        <div>
          <p className="submitted-at">
            Submitted at {this.state.article.submittedAt}
          </p>
          <p>
            <cite className="img-source">{this.state.article.imgSource}</cite>
          </p>
          <p className="category">{this.state.article.category}</p>
        </div>
        <div className="article-content">
          <p>{this.state.article.text}</p>
        </div>

        {isAuthenticated ? editDeleteButtons : <div />}
      </div>
    );
  }
}

Show.propTypes = {
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { setCurrentUser }
)(Show);
