import React from "react";
import "./App.scss";
import queryString from "query-string";
import { BrowserRouter as Router, Route } from "react-router-dom";
import SideBar from "../src/components/Sidebar";
import CreateArticle from "../src/components/articles/Create-Article";
import AllArticles from "../src/components/articles/AllArticles";
import Header from "../src/components/Header";
import Footer from "../src/components/Footer";
import Show from "../src/components/articles/Show";
import Edit from "../src/components/articles/Edit";

class App extends React.Component {
  componentDidMount() {
    var query = queryString.parse(this.props.location.search);
    if (query.token) {
      window.localStorage.setItem("jwt", query.token);
      this.props.history.push("/");
    }
  }
  render() {
    return (
      <Router>
        <div id="page-wrap">
          <SideBar />
          <Header />
          <Route path="/" exact component={AllArticles} />
          <Route path="/api/articles/show/:id" exact component={Show} />
          <Route path="/api/articles/add" exact component={CreateArticle} />
          <Route path="/api/articles/edit/:id" exact component={Edit} />
          <Footer />
        </div>
      </Router>
    );
  }
}

export default App;
